mapper.update:
UPDATE board_tb
SET title = #{title}, category = #{categoryNo}, content = #{content}
WHERE uid = #{uid};

UPDATE member_tb
SET nickname = #{nickName}
WHERE email = #{email};

UPDATE board_tb
SET view = view + 1
WHERE uid = #{uid};

UPDATE comment_tb
SET content = #{content}
WHERE uid = #{uid};

UPDATE reply_tb
SET content = #{content}
WHERE uid = #{uid};
